int SEC ("xdp")
xdp_root (struct xdp_md *ctx) {
    __u32 *fd;
#pragma clang loop unroll(full)
    for (__u32 i = 0; i < ROOT_ARRAY_SIZE; i++) {
        bpf_tail_call (ctx, & root_array, i);
    }
    return XDP_PASS;
}

