static __always_inline void *get_ct_map6 (const struct ipv6_ct_tuple *tuple) {
    if (tuple->nexthdr == IPPROTO_TCP)
        return &CT_MAP_TCP6;
    return &CT_MAP_ANY6;
}

static __always_inline void *get_ct_map4 (const struct ipv4_ct_tuple *tuple) {
    if (tuple->nexthdr == IPPROTO_TCP)
        return &CT_MAP_TCP4;
    return &CT_MAP_ANY4;
}
